CREATE TABLE odbvue.crm_products (
    id          NUMBER(19, 0) GENERATED BY DEFAULT AS IDENTITY NOT NULL ENABLE,
    guid        RAW(16) DEFAULT sys_guid() NOT NULL ENABLE,
    code        VARCHAR2(200 CHAR) NOT NULL ENABLE,
    name        VARCHAR2(200 CHAR) NOT NULL ENABLE,
    description VARCHAR2(2000 CHAR),
    price       NUMBER(15, 2) NOT NULL ENABLE,
    status      CHAR(1 CHAR) DEFAULT 'A' NOT NULL ENABLE,
    created     TIMESTAMP(6) DEFAULT systimestamp NOT NULL ENABLE,
    modified    TIMESTAMP(6) DEFAULT systimestamp
);

ALTER TABLE odbvue.crm_products
    ADD CONSTRAINT chk_crm_products_status
        CHECK ( status IN ( 'A', 'I' ) ) ENABLE;

ALTER TABLE odbvue.crm_products
    ADD CONSTRAINT cpk_crm_products PRIMARY KEY ( id )
        USING INDEX ENABLE;

ALTER TABLE odbvue.crm_products ADD CONSTRAINT cuq_crm_products_code UNIQUE ( code )
    USING INDEX ENABLE;

ALTER TABLE odbvue.crm_products ADD CONSTRAINT cuq_crm_products_guid UNIQUE ( guid )
    USING INDEX ENABLE;

