CREATE TABLE odbvue.crm_survey_responses (
    id        NUMBER(19, 0) GENERATED BY DEFAULT AS IDENTITY NOT NULL ENABLE,
    survey_id NUMBER(19, 0) NOT NULL ENABLE,
    responses CLOB,
    author    CHAR(32 CHAR),
    created   TIMESTAMP(6) DEFAULT systimestamp NOT NULL ENABLE
);

ALTER TABLE odbvue.crm_survey_responses ADD CONSTRAINT csc_crm_survey_responses_json CHECK ( responses IS JSON ) ENABLE;

