CREATE TABLE odbvue.app_labels (
    id   NUMBER GENERATED BY DEFAULT AS IDENTITY NOT NULL ENABLE,
    name VARCHAR2(200 CHAR) NOT NULL ENABLE,
    code VARCHAR2(200 CHAR) GENERATED ALWAYS AS ( upper(replace(name, ' ', '_')) ) VIRTUAL
);

ALTER TABLE odbvue.app_labels ADD PRIMARY KEY ( id )
    USING INDEX ENABLE;


