CREATE TABLE odbvue.app_roles (
    id          NUMBER(19, 0) GENERATED BY DEFAULT AS IDENTITY NOT NULL ENABLE,
    role        VARCHAR2(200 BYTE) NOT NULL ENABLE,
    description VARCHAR2(2000 BYTE)
);

ALTER TABLE odbvue.app_roles ADD PRIMARY KEY ( id )
    USING INDEX ENABLE;

ALTER TABLE odbvue.app_roles ADD UNIQUE ( role )
    USING INDEX ENABLE;


