CREATE TABLE odbvue.crm_survey_questions (
    id        NUMBER(19, 0) GENERATED BY DEFAULT AS IDENTITY NOT NULL ENABLE,
    survey_id NUMBER(19, 0) NOT NULL ENABLE,
    position  NUMBER(10, 0) NOT NULL ENABLE,
    question  CLOB,
    type      VARCHAR2(20 CHAR) DEFAULT 'free text' NOT NULL ENABLE,
    required  CHAR(1 CHAR) DEFAULT 'N' NOT NULL ENABLE
);

ALTER TABLE odbvue.crm_survey_questions
    ADD CONSTRAINT csc_crm_survey_questions_req
        CHECK ( required IN ( 'Y', 'N' ) ) ENABLE;

ALTER TABLE odbvue.crm_survey_questions
    ADD CONSTRAINT csc_crm_survey_questions_type
        CHECK ( type IN ( 'free text', 'number', 'single choice', 'multiple choices', 'rating 5',
                          'none' ) ) ENABLE;

ALTER TABLE odbvue.crm_survey_questions
    ADD CONSTRAINT pk_crm_survey_questions PRIMARY KEY ( id )
        USING INDEX ENABLE;


